import React from 'react';
import { connect } from 'react-redux';

import CollectionItem from '../../components/collection-item/collection-item.component';

import { selectCollection } from '../../redux/shop/shop.selectors';

import './collection.styles.scss';

const CollectionPage = ({ match }) => (
  <div className="collection-page">
    <h2>CollectionPage</h2>
  </div>
);

const mapStateToProps = (state, ownProps) => ({
  // ** Notice the selectCollection func returns a createSelector func (can see shop.selectors.js)
  // so you have to pass in state as argument second invocation, which is equivalent to createSelector(state)
  collection: selectCollection(ownProps.match.prams.collectionId)(state),
});

export default CollectionPage;
