// const OptionContainerStyles = css`
//   padding: 10px 15px;
//   cursor: pointer;
// `;
// export const OptionLink = styled(Link)`
//   ${OptionContainerStyles}
// `;
// export const OptionDiv = styled.div`
//   ${OptionContainerStyles}
// `;

// method 2
// pass as="div" as props when using <OptionLink as="div"></OptionLink> later
export const OptionLink = styled(Link)`
  padding: 10px 15px;
  cursor: pointer;
`;
